import React, {useState} from 'react'
import {
    Box, Grid, Card, Button, withStyles, makeStyles, Table, TableBody, TableCell,
    TableContainer, TableHead, TableRow, TextField, Typography,
} from '@material-ui/core';
import data from './resources/names'


const useStyles = makeStyles({
    table: {
        width: "50%",
    },
    card: {
        background: "white",
        width: "50%",
    },
    container: {
        width: 500,
    },
    button: {
        padding: '30px',
    },
});

const StyledTableCell = withStyles((theme) => ({
    head: {
        backgroundColor: theme.palette.common.black,
        color: theme.palette.common.white,
    },
    body: {
        fontSize: 14,
    },
}))(TableCell);

export const NameTable = () => {

    const classes = useStyles;

    const names = data.names;

    const [nameOrder, setNameOrder] = useState(true);
    const [buttonText, setButtonText] = useState("Order by amount");

    var total = 0;

    const sortNames = () => {
        if (nameOrder) {
            names.sort((a, b) => (a.name > b.name) ? 1 : -1)
        }
        else {
            names.sort((a, b) => (a.amount < b.amount) ? 1 : -1)

        }
    }


    const countTotal = () => {
        for (var i = 0; i < names.length; i++) {
            total += names[i].amount;
        }
    }

    const buttonHandler = () => {
        if (nameOrder) {
            setNameOrder(false);
            setButtonText("order by amount");
        }else {
            setNameOrder(true);
            setButtonText("order by name");
        }
    }

    sortNames();
    countTotal();

    return (

        <Box display="flex" justifyContent="center" m={1} p={1}>
<Box width = "50%">
        <Card className={classes.card}>
        <Box display="flex" justifyContent="center" m={1} p={1}>

                    <Typography variant="h2">Name Application</Typography>
                   <br />
                    <Typography variant="subtitle2">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent sodales feugiat nibh et elementum. Suspendisse eu porttitor urna. Fusce ullamcorper turpis diam, vel gravida elit sagittis bibendum. Vestibulum scelerisque sem at condimentum ultrices.
</Typography>
</Box>

                    <br />
<Button onClick={buttonHandler}>{buttonText}</Button>
    <TextField variant="filled" label="Search"/>




                <TableContainer className={classes.container}>
                    <Table className={classes.table} aria-label="simple table">
                        <TableHead>
                            <TableRow>
                                <StyledTableCell>Name</StyledTableCell>
                                <StyledTableCell align="right">Amount</StyledTableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {names.map((s, i) => (
                                <TableRow key={i}>
                                    <TableCell component="th" scope="row">
                                        {s.name}
                                    </TableCell>
                                    <TableCell align="right">{s.amount}</TableCell>
                                </TableRow>
                            ))}
                            <TableRow>
                                <StyledTableCell>Total</StyledTableCell>
                                <StyledTableCell align="right">{total}</StyledTableCell>
                            </TableRow>
                        </TableBody>
                    </Table>
                </TableContainer>

        </Card>
</Box></Box>

    );
};
