import React, { useState } from 'react'
import { Box, Grid, Paper, makeStyles, TextField, Typography, Divider} from '@material-ui/core';
import data from './resources/names';
import NameTable from './nameTable';



const useStyles = makeStyles({
    paper: {
        background: "white",
    },
});



export const FrontPage = () => {

    const classes = useStyles;

    const names = data.names;
    var total = 0;

    const countTotal = () => {
        for (var i = 0; i < names.length; i++) {
            total += names[i].amount;
        }
    }
    countTotal();

    const [amountText, setAmountText] = useState("Total amount: " + total);


    const searchName = (search) => {
        console.log("searchName " + search);
        if (search !== "") {

            var found = -1;

            for (var i = 0; i < names.length; i++) {
                if (names[i].name === search) {
                    found = i;
                    console.log("FOUND");
                }
            }

            if (found === -1) {
                setAmountText("Nothing found");
            }
            else {
                setAmountText("Amount: " + names[found].amount);
            }
        }
        else {
            setAmountText("");
        }
        if (!search.replace(/\s/g, '').length) {
            setAmountText("Total amount: " + total);
        }
    }



    return (
        <Box display="flex" justifyContent="center" m={1} p={1} height="50%">
            <Box width="35%" >
                <Paper className={classes.paper}>
                    <Box display="flex" justifyContent="center" m={1} p={1}>

                       <Typography variant="h2">Name Application</Typography>
                    </Box>
                    <Box display="flex" justifyContent="center" m={1} p={1}>

                    <Grid container>
                    <Grid container item sm={12} align="center">
                    <Box display="flex" m={1} p={1}>
                        <Typography variant="caption">This application lets you view a list of names stored in a json file. You can search for a name, change how the names are ordered and choose how many rows are shown per page.</Typography>
                    </Box>
                    </Grid>




                            <Grid container item sm={6}>
                                <TextField variant="outlined" label="Search" size="small" onChange={(event) => searchName(event.target.value)} />
                            </Grid>

                            <Grid container item sm={6} alignItems="center" justify="flex-end">
                                <Typography variant="subtitle2">{amountText}</Typography>
                            </Grid></Grid></Box>

                    <NameTable
                    names={names}
                    />

                </Paper>
            </Box>
        </Box>

    );
};
