import React, {useState} from 'react'
import {
    Grid, Card, Button, withStyles, makeStyles, Table, TableBody, TableCell,
    TableContainer, TableHead, TableRow, TableSortLabel
} from '@material-ui/core';
import data from './resources/names'


const useStyles = makeStyles({
    table: {
        width: "50%",
    },
    card: {
        background: "white",
        width: "50%",
    },
    container: {
        width: 500,
    },
    button: {
        padding: '30px',
    },
});

const StyledTableCell = withStyles((theme) => ({
    head: {
        backgroundColor: theme.palette.common.black,
        color: theme.palette.common.white,
    },
    body: {
        fontSize: 14,
    },
}))(TableCell);

export const NameTable = () => {

    const classes = useStyles;

    const names = data.names;

    const [nameOrder, setNameOrder] = useState(true);
    var total = 0;

    const sortNames = () => {

        if (nameOrder) {
            names.sort((a, b) => (a.name > b.name) ? 1 : -1)
        }
        else {
            names.sort((a, b) => (a.amount < b.amount) ? 1 : -1)

        }
    }

    const countTotal = () => {
        for (var i = 0; i < names.length; i++) {
            total += names[i].amount;
        }
    }

    countTotal();

    const buttonHandler = () => {
        if (nameOrder) {
            setNameOrder(false);
        }else {
            setNameOrder(true);
        }
    }

    console.log(names[0]);

    sortNames();

    return (



        <Card className={classes.card}>
            <Grid
                container
                direction="row"
                justify="center"
                alignItems="center"
                spacing={3}
            >
                <Grid container item xs={6}>
                    <Button className={classes.button} variant="outlined" color="primary" disabled={nameOrder} onClick={buttonHandler}>
                        Order by name
    </Button>

                    <Button className={classes.button} variant="outlined" color="primary" disabled={!nameOrder} onClick={buttonHandler}>
                        Order by amount
    </Button>
                </Grid>



                <TableContainer className={classes.container}>
                    <Table className={classes.table} aria-label="simple table">
                        <TableHead>
                            <TableRow>
                                <StyledTableCell>Name</StyledTableCell>
                                <StyledTableCell align="right">Amount</StyledTableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {names.map((s, i) => (
                                <TableRow key={i}>
                                    <TableCell component="th" scope="row">
                                        {s.name}
                                    </TableCell>
                                    <TableCell align="right">{s.amount}</TableCell>
                                </TableRow>
                            ))}
                            <TableRow>
                                <StyledTableCell>Total</StyledTableCell>
                                <StyledTableCell align="right">{total}</StyledTableCell>
                            </TableRow>
                        </TableBody>
                    </Table>
                </TableContainer>
            </Grid>
        </Card>


    );
};
