import React, { useState } from 'react'
import {
    Box, Paper, withStyles, makeStyles, Table, TableBody, TableCell,
    TableContainer, TableHead, TableRow, TextField, Typography, TableSortLabel, TablePagination
} from '@material-ui/core';
import data from './resources/names'


const useStyles = makeStyles({
    paper: {
        background: "white",

    },
});

const StyledTableCell = withStyles((theme) => ({
    head: {
        backgroundColor: '#0f9dc3',
        color: theme.palette.common.white,
    },
    body: {
        fontSize: 14,
    },
}))(TableCell);


export const FrontPage = () => {

    const classes = useStyles;

    const names = data.names;

    const [orderBy, setOrderBy] = useState("amount");
    const [foundText, setFoundText] = useState("");
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);


    var total = 0;

    const sortNames = () => {
        if (orderBy === "name") {
            names.sort((a, b) => (a.name.localeCompare(b.name)))
        }
        else {
            names.sort((a, b) => (b.amount - a.amount || a.name.localeCompare(b.name)))

        }
    }


    const countTotal = () => {
        for (var i = 0; i < names.length; i++) {
            total += names[i].amount;
        }
    }

    const handleSort = () => {
        console.log("sorting");
        if (orderBy === "name") {
            setOrderBy("amount");
        }
        else {
            setOrderBy("name");
            console.log(orderBy);
        }
    }

    const handleChangePage = (event, newPage) => {
        setPage(newPage);
      };

      const handleChangeRowsPerPage = (event) => {
        setRowsPerPage(parseInt(event.target.value, 10));
        setPage(0);
      };

    const searchName = (search) => {
        console.log("searchName " + search);
        if (search !== "") {

            var found = -1;

            for (var i = 0; i < names.length; i++) {
                if (names[i].name === search) {
                    found = i;
                    console.log("FOUND");
                }
            }

            if (found === -1) {
                setFoundText("There are none with that name");
            }
            else {
                setFoundText("Found " + names[found].amount + " of that name");
            }
        }
        else {
            setFoundText("");
        }
        if (!search.replace(/\s/g, '').length) {
            setFoundText("");
        }
    }

    sortNames();
    countTotal();

    return (
        <Box display="flex" justifyContent="center" m={1} p={1} height="50%">
            <Box width="35%" >
                <Paper className={classes.paper}>
                    <Box display="flex" justifyContent="center" m={1} p={1}>

                        <Typography variant="h2">Name Application</Typography>
                    </Box><Box display="flex" justifyContent="center" m={1} p={1}>
                        <Typography variant="subtitle2">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent sodales feugiat nibh et elementum. Suspendisse eu porttitor urna. Fusce ullamcorper turpis diam, vel gravida elit sagittis bibendum. Vestibulum scelerisque sem at condimentum ultrices.</Typography>
                    </Box>
                    <Box className={classes.search} m={1} p={1}><TextField variant="outlined" label="Search" size="small" onChange={(event) => searchName(event.target.value)} /> <Typography variant="overline">{foundText}</Typography></Box>
                    <TableContainer>
                        <Table className={classes.table} stickyHeader>
                            <TableHead>
                                <TableRow>
                                    <StyledTableCell>


                                        <TableSortLabel
                                            active={orderBy === "name"}
                                            onClick={handleSort}>
                                            Name
                                                </TableSortLabel>
                                    </StyledTableCell>
                                    <StyledTableCell align="right"><TableSortLabel
                                        active={orderBy === "amount"}
                                        onClick={handleSort}>
                                        Amount
                                        </TableSortLabel>
                                    </StyledTableCell>
                                </TableRow>
                                <TableRow>
                                    <StyledTableCell><Typography variant="overline">Total</Typography></StyledTableCell>
                                    <StyledTableCell align="right"><Typography variant="overline">{total}</Typography></StyledTableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {names
                                .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                .map((s, i) => (
                                    <TableRow key={i}>
                                        <TableCell component="th" scope="row">
                                            {s.name}
                                        </TableCell>
                                        <TableCell align="right">{s.amount}</TableCell>
                                    </TableRow>
                                ))}

                            </TableBody>
                        </Table>
                    </TableContainer>
                    <TablePagination
          rowsPerPageOptions={[5, 10, 20]}
          component="div"
          count={names.length}
          rowsPerPage={rowsPerPage}
          page={page}
          onChangePage={handleChangePage}
          onChangeRowsPerPage={handleChangeRowsPerPage}
        />

                </Paper>
            </Box></Box>

    );
};
