import React, { useState } from 'react'
import { Box, Grid, Paper, makeStyles, TextField, Typography} from '@material-ui/core';
import data from './resources/names';
import NameTable from './nameTable';
import { ThemeProvider, createMuiTheme } from '@material-ui/core/styles';

const theme = createMuiTheme({
  typography: {
    fontFamily: [
      'Yellowtail',
    ].join(','),
  },});



const useStyles = makeStyles({
    paper: {
        background: "white",
    },
});



export const FrontPage = () => {

    const classes = useStyles;

    const names = data.names;
    var total = 0;

    const countTotal = () => {
        for (var i = 0; i < names.length; i++) {
            total += names[i].amount;
        }
    }
    countTotal();

    const [amountText, setAmountText] = useState("Total amount: " + total);


    const searchName = (search) => {
        console.log("searchName " + search);
        if (search !== "") {

            var found = -1;

            for (var i = 0; i < names.length; i++) {
                if (names[i].name === search) {
                    found = i;
                    console.log("FOUND");
                }
            }

            if (found === -1) {
                setAmountText("There are none with that name");
            }
            else {
                setAmountText("Found " + names[found].amount + " of that name");
            }
        }
        else {
            setAmountText("");
        }
        if (!search.replace(/\s/g, '').length) {
            setAmountText("Total amount: " + total);
        }
    }



    return (
        <Box display="flex" justifyContent="center" m={1} p={1} height="50%">
            <Box width="35%" >
                <Paper className={classes.paper}>
                    <Box display="flex" justifyContent="center" m={1} p={1}>

                        <ThemeProvider theme={theme}><Typography variant="h2">Name Application</Typography></ThemeProvider>
                    </Box><Box display="flex" justifyContent="center" m={1} p={1}>
                        <Typography variant="subtitle2">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent sodales feugiat nibh et elementum. Suspendisse eu porttitor urna. Fusce ullamcorper turpis diam, vel gravida elit sagittis bibendum. Vestibulum scelerisque sem at condimentum ultrices.</Typography>
                    </Box>

                    <Box display="flex" justifyContent="center" m={1} p={1}>

                        <Grid container>
                            <Grid container item sm={6}>
                                <TextField variant="outlined" label="Search" size="small" onChange={(event) => searchName(event.target.value)} />
                            </Grid>

                            <Grid container item sm={6} alignItems="center" justify="flex-end">
                                <Typography variant="subtitle2">{amountText}</Typography>
                            </Grid></Grid></Box>

                    <NameTable
                                  names={names}
                                  />

                </Paper>
            </Box>
        </Box>

    );
};
